<template>
  <VueDragResize ref="vdr" class="vdr-panel" :isActive="active" preventActiveBehavior :parentLimitation="false" :isDraggable="false"
                 :minw="0" :minh="0" :w="width" :h="height" :sticks="sticks ? [sticks] : []"
                 @resizing="onResize" @resizestop="onStopResize">
    <div class="resizable-panel-content" :class="{active}">
      <slot/>
    </div>
  </VueDragResize>
</template>

<script>
import VueDragResize from '@/library/vue-drag-resize'

export default
{
  name: 'ResizablePanel',
  components:
    {
      VueDragResize,
    },
  props:
    {
      active:
        {
          type: Boolean,
          required: true,
        },
      sticks:
        {
          type: String,
          default: '',
        },
      width:
        {
          type: Number,
          default: 500,
        },
      height:
        {
          type: Number,
          default: 500,
        },
    },
  methods:
    {
      onResize (newRect) {
        this.$store.commit('panes/setResizing', true)
        this.$emit('resize', newRect)
      },
      onStopResize () {
        this.$store.commit('panes/setResizing', false)
        this.$emit('stopresize')
      },
    },
}
</script>

<style lang="scss">
  @import '@/assets/sass/_0.declare.scss';

  .vdr.vdr-panel
  {
    position: relative;
    max-width: 100vw;
    max-height: calc(100vh - 6em); /* exclude header */
    top: 0 !important;
    left: 0 !important;

    &::before
    {
      z-index: -2;
    }

    .vdr-stick
    {
      z-index: 4;
      width: 38px !important;
      height: 38px !important;
      border-radius: 19px;
      opacity: 1;
      cursor: grab;
      background-image:  url('data:image/png;base64,');
      background-color: $appyellow;
      background-repeat: no-repeat;
      background-position: 5px 5px;
      border:none;
      -webkit-box-shadow: 0 0.1rem 0.2rem 0 rgba(30, 34, 36, 0.1);
      box-shadow: 0 0.1rem 0.2rem 0 rgba(30, 34, 36, 0.1);
    }
    .vdr-stick:hover
    {
      opacity: 0.3;
    }
    .vdr-stick-tr
    {
      top: -2px !important;
      right: -19px !important;
    }

    .vdr-stick-mr
    {
      right: -19px !important;
      margin-top: -4px !important;
    }

    .vdr-stick-bl
    {
      bottom: -25px !important;
      left: -25px !important;
    }

    .vdr-stick-bm
    {
      bottom: -36px !important;
      margin-left: -19px !important;
    }

    .vdr-stick-br
    {
      bottom: -36px !important;
      right: -19px !important;
    }
  }

  .resizable-panel-content
  {
    min-height: 0;
    height: 100%;
    overflow: hidden;
    display: flex;
    flex-direction: column;
  }

  .resizable-panel-content.active
  {
    border: 1px dashed red;
  }

  .resizable-panel-content > *:nth-child(2)
  {
    flex: 1 1 0;
  }

</style>
